mapper.select:

select id, open_id, date,arena_id,court_id, start, end, charged, fee,update_time
from tt_booking
where id = #{id,jdbcType=INTEGER}

select a.id, a.open_id, a.date, a.start, a.end,a.fee, a.charged, a.update_time, b.nickname,b.wx_nickname,b.avatar,b.vip, d.open_id as share_user_open_id, d.nickname as share_user_nickname, d.wx_nickname as share_user_wx_nickname, d.avatar as share_user_avatar, d.vip as share_user_vip from (select id, open_id, date,arena_id,court_id, start, end,fee, charged, update_time from tt_booking
open_id=#{openId} and id < #{id}
order by id desc limit 21) a inner join tt_user b on a.open_id=b.open_id left join booking_share c on a.id=c.booking_id left join tt_user d on c.open_id=d.open_id

select a.id, a.open_id, a.date, a.arena_id,a.court_id, a.start, a.end,a.fee, a.charged, a.update_time, b.nickname,b.wx_nickname,b.avatar,b.vip, d.open_id as share_user_open_id, d.nickname as share_user_nickname, d.wx_nickname as share_user_wx_nickname, d.avatar as share_user_avatar, d.vip as share_user_vip from tt_booking a inner join tt_user b on a.open_id=b.open_id left join booking_share c on a.id=c.booking_id left join tt_user d on c.open_id=d.open_id
a.open_id=#{openId}
and a.date >= #{startDate} and a.date < #{endDate}
order by a.update_time

mapper.delete:
delete from tt_booking

    id = #{id}

        and open_id=#{openId}

mapper.select:
select a.id, a.open_id, a.date, a.start, a.end,a.fee, a.charged, a.update_time, d.open_id as share_user_open_id, d.nickname as share_user_nickname, d.wx_nickname as share_user_wx_nickname, d.avatar as share_user_avatar, d.vip as share_user_vip, e.name as arena_name, f.name as court_name from (select id, open_id, date,arena_id,court_id, start, end,fee, charged, update_time from tt_booking
open_id=#{openId} and status='ok' and date < #{now} and date >= #{now}
order by date desc order by date asc limit 20) a left join booking_share c on a.id=c.booking_id left join tt_user d on c.open_id=d.open_id left join arena e on a.arena_id=e.id left join court f on a.court_id=f.id

mapper.update:
update tt_booking
set status=#{status}
where pay_no = #{tradeNo}
